ng: 10px;\r\n    overflow: hidden;\r\n    width: 100%;\r\n    color: #171717;\r\n}\r\n\r\n.wpcf7 textarea {\r\n	height: 150px;\r\n}\r\n\r\n/* backgroud Modification */\r\n/***********************************************************************************************************/\r\n.main-content {\r\n    background: url("http://automatedquiz.com/quizstorm/wp-content/uploads/2017/11/Rough-Grey-Tilable-Pattern-For-Website-Background.jpg");\r\n    padding: 30px 30px 0;\r\n}\r\n\r\n.filter-bar {\r\n    background: url("http://automatedquiz.com/quizstorm/wp-content/uploads/2017/11/Rough-Grey-Tilable-Pattern-For-Website-Background.jpg");\r\n    -webkit-box-shadow: 0 2px 3px rgba(0,0,0,0.1);\r\n    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.56);\r\n    z-index: 100;\r\n    position: relative;\r\n}\r\n\r\n.logo-bar {\r\n    background: #fff;\r\n    -webkit-box-shadow: 0 2px 3px rgba(0,0,0,0.1);\r\n    box-shadow: 0px 5px 23px rgba(0, 0, 0, 0.56);\r\n}\r\n\r\n.filter-item {\r\n    padding-left: 26px;\r\n    padding-right: 30px;\r\n    line-height: 81px;\r\n    border: 1px;\r\n}\r\n\r\n.bod {\r\n    background-color: #fff0;\r\n    color: #6222;\r\n    text-align: center;\r\n    font-family: "Segoe UI Light", "Sourse Sans Pro", sans-serif;\r\n}\r\n\r\n.related-posts {\r\n    padding: 30px 0px 0px;\r\n    margin: 50px 0;\r\n    border-top: solid 0px #e9e9e9;\r\n}\r\n\r\n\r\n/***********************************************************************************************************/\r\n\r\n.media-files span {\r\n    font-size: 24px;\r\n    font-weight: 400;\r\n    display: none;\r\n}\r\n\r\n.full-page-bg {\r\n    position: fixed;\r\n    top: 0;\r\n    bottom: 0;\r\n    left: 0;\r\n    right: 0;\r\n    background-color: #fff0;\r\n}\r\n\r\n.thumbnail .caption > .post-by, .thumbnail .caption > .post-time {\r\n    color: #7c7d7f;\r\n    display: none;\r\n}\r\n\r\n.post-type-icon {\r\n	display: none;\r\n}\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r